import React from 'react'
const async = require("async");

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
    <Header name={course}/>
    <Content pt1={part1} es1={exercises1} pt2={part2} es2={exercises2} 
    pt3={part3} es3={exercises3}/>
    <Total sum={exercises1+exercises2+exercises3}/>
    </div>
  )
}

const Header = (props) =>{
  return(<>
    <p><center>
      <h2>{props.name}</h2>
    </center>
    </p>

  </>)
}

const Part = (props) =>{
  return(<>
    <p><center>
      <b>Part {props.num}:</b> {props.pt} - {props.es} exercises
    </center>
    </p>
  </>)
}



const Content = (props) =>{
  return(
    <>
      <Part num="1" pt={props.pt1} es={props.es1}/>
      <Part num="2" pt={props.pt2} es={props.es2}/>
      <Part num="3" pt={props.pt3} es={props.es3}/>
    </>
    );

}

const Total = (props) =>{
  return(
    <>
    <center>
      <br />For a total of {props.sum} exercises
    </center>
    </>
  )

} 

export default App
