/* All data still resides in the App component, which passes the necessary data to each 
component using props. Header takes care of rendering the name of the course, Content 
renders the parts and their number of exercises and Total renders the total number of 
exercises. */

import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <>
    <Header name={course}/>
    <Content pt1={part1} es1={exercises1} pt2={part2} es2={exercises2} 
    pt3={part3} es3={exercises3}/>
    <Total sum={exercises1+exercises2+exercises3}/>
    </>
  )
}

const Header = (props) =>{
  return(<>
    <p><center>
      <h2>{props.name}</h2>
    </center>
    </p>

  </>)
}

const Content = (props) =>{
    return(
      <>
      <center>
        <b>Part 1:</b> {props.pt1} - {props.es1} exercises<br/>
        <b>Part 2:</b> {props.pt2} - {props.es2} exercises<br/>
        <b>Part 1:</b> {props.pt3} - {props.es3} exercises<br/>
      </center>
      </>
    )
}

const Total = (props) =>{
  return(
    <>
    <center>
      <br /><br />For a total of {props.sum} exercises
    </center>
    </>
  )

} 

export default App
