import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header name={course}/>
      <Content part={parts}/>
      <Total part={parts}/>
    </div>
  )
}

const Header = (props) =>{
  return(<>
    <p><center>
      <h2>{props.name}</h2>
    </center>
    </p>

  </>)
}

const Part = (props) =>{
  return(<>
    <p><center>
      <b>Part {props.num}:</b> {props.pt} - {props.es} exercises
    </center>
    </p>
  </>)
}



const Content = (props) =>{
  return(
    <>
      <Part num="1" pt={props.pt1} es={props.es1}/>
      <Part num="2" pt={props.pt2} es={props.es2}/>
      <Part num="3" pt={props.pt3} es={props.es3}/>
    </>
    );

}

const Total = (props) =>{
  return(
    <>
    <center>
      <br />For a total of {props.sum} exercises
    </center>
    </>
  )

} 

export default App
