import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header name={course}/>
      <Content part={parts}/>
      <Total part={parts}/>
    </div>
  )
}

const Header = (props) =>{
  return(<>
    <p><center>
      <h2>{props.name}</h2>
    </center>
    </p>

  </>)
}

const Part = (props) =>{
  try{
  return(<>
    <p><center>
      <b>Part {props.num}:</b> {props.pt.name} - {props.pt.exercises} exercises
    </center>
    </p>
  </>)
  }catch (err){
    console.log(err.message + " in Part");
  }
}



const Content = (props) =>{
  console.log(props[0]);
  return(
    <>
      <Part num="1" pt={props.part[0]}/>
      <Part num="2" pt={props.part[1]}/>
      <Part num="3" pt={props.part[2]}/>
    </>
    );

}

const Total = (props) =>{
  console.log(props);
  try{
  return(
    <>
    <center>
        <br />For a total of {props.part[0].exercises + props.part[1].exercises + props.part[2].exercises} exercises
    </center>
    </>
  )
  } catch(err){
    console.log(err.message + " in Total");
  }
} 

export default App
