import React, { useState } from 'react'

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  let total = good + neutral + bad;
  let positiveFeedback = (good/total)*100;

  return (
    <div>
    <center>
      <h1>Give your feedback!</h1>
      <button onClick={()=>Increment(good, setGood)}>Good! :)</button>
      <button onClick={()=>Increment(neutral, setNeutral)}>Neutral :|</button>
      <button onClick={()=>Increment(bad, setBad)}>Bad! :(</button>
      <h1>Statistics:</h1>
      Good: {good} <br></br>
      Neutral: {neutral} <br></br>
      Bad: {bad} <br></br><br></br>

      Average score: <br></br>
      Positive feedback: {positiveFeedback}%<br></br>
      </center>
    </div>
  )
}

const Increment = (value, setValue) =>{
  return(
    setValue(value +1)
  );
}

export default App
