import React, { useState } from 'react'

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)



  return (
    <div>
    <center>
      <h1>Give your feedback!</h1>
      <button onClick={()=>Increment(good, setGood)}>Good! :)</button>
      <button onClick={()=>Increment(neutral, setNeutral)}>Neutral :|</button>
      <button onClick={()=>Increment(bad, setBad)}>Bad! :(</button>
      <Statistics good={good} neutral={neutral} bad={bad}/>
      </center>
    </div>
  )
}

const Increment = (value, setValue) =>{
  return(
    setValue(value +1)
  );
}

const Statistics = ({good, neutral, bad})=>{

  if((good==0 && neutral==0) && bad==0){
    return (<div>
      <center>
      <br></br>
        No feedback given. Click on any button to give feedback!
      </center>
    </div>);
  } else{
    let total = good + neutral + bad;
    let positiveFeedback = (good / total) * 100;
    let average = (good - bad) / total;

    return (
      <div>
        <h1>Statistics:</h1>
    Good: { good} <br></br>
    Neutral: { neutral} <br></br>
    Bad: { bad} <br></br><br></br>

    All: { total} <br></br>
    Average score: { average} <br></br>
    Positive feedback: { positiveFeedback}% <br></br>
      </div>
    )
  }
}

export default App
