import React from 'react'

const Course = ({course}) => {
    const totalEs = course.parts.reduce((tot,part)=>{return tot + part.exercises},0)
    return (
        <div>
            <h1>{course.name}</h1>
            {course.parts.map((part) =>{
                return(
                <li key={part.id}>
                    {part.name} | Exercises = {part.exercises}
                </li>
                )}
            )}
            <br/>
            Total exercises = {totalEs}
        </div>
    )
}

export default Course
