import React, { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')


const addNewName = (event) => {
  event.preventDefault()
  let content = document.getElementById("name").value
  console.log(content);
  return setNewName(content)}


const updateList = (event) => {
  event.preventDefault();

  const searchResult = persons.find((element) => {
    return element['name'] == newName})

  console.log(searchResult);
  if (searchResult) {
    return(window.alert("This name is already present!"))
  } else {
    return setPersons(persons.concat({ 'name': newName }))
  }
}

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={updateList}>
        <div>
          Name: <input id="name" 
          onChange={addNewName} />
        </div>
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <Numbers list={persons}/>
    </div>
  )
}

const Numbers = ({list}) =>{
  return(
    <div>
    {list.map((entry)=>{
      return(
      <li key={entry.name}>
        {entry.name}
      </li>)
    })}
    </div>
  )
}

export default App
