import React, { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')


const addNew = (event, handleState) => {
  console.log(handleState)
  event.preventDefault()
  let id = console.log(event.target.id)
  let content = document.getElementById(id).value
  console.log(content);

  return handleState(content)}


const updateList = (event) => {
  event.preventDefault();

  const searchResult = persons.find((element) => (element['name'] === newName))

  if (searchResult) {
    return(window.alert('The name ' + newName + ' is already present!'))
  } else {
    return setPersons(persons.concat({ 'name': newName }))
  }
}

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={updateList}>
        <div>
        Name: <input id="name" 
        onChange={(e) => this.addNew(e, setNewName)} /><br/>        
        Phone number: <input id="number" 
        onChange={(e) => this.addNew(e, setNewNumber)} />
        </div>
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <Numbers list={persons}/>
    </div>
  )
}

const Numbers = ({list}) =>{
  return(
    <div>
    {list.map((entry)=>{
      return(
      <li key={entry.name}>
        {entry.name}
      </li>)
    })}
    </div>
  )
}

export default App
