import React, { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])

const [newName, setNewName] = useState('')
const [newNumber, setNewNumber] = useState('')
const [searchResults, setSearchResults] = useState([])


const addNew = (event, handleState, id) => {
  console.log(handleState)
  event.preventDefault()
  let content = document.getElementById(id).value
  console.log(content);

  return handleState(content)}


const updateList = (event) => {
  event.preventDefault();

  const personSearch = persons.find((element) => (element['name'] === newName))

  if (personSearch) {
    return(window.alert('The name ' + newName + ' is already present!'))
  } else {
    return setPersons(persons.concat({ 'name': newName, 'number': newNumber}))
  }
}

const searchList = () => {
  let searched = document.getElementById("search").value
  let results = persons.filter((element)=>{
   return (element['name'].search(searched)> -1) ? true : false
  })
  return setSearchResults(results)
}

  return (
    <div>
      <h2>Search</h2>
      <input id="search" onChange={searchList}/><br/>
      Results: {<Found results={searchResults}/>}
      <h2>Phonebook</h2>
      <form onSubmit={updateList}>
        <div>
        Name: <input id="name" 
        onChange={(e) => addNew(e, setNewName, "name")} /><br/>        
        Phone number: <input id="number" 
        onChange={(e) => addNew(e, setNewNumber, "number")} />
        </div>
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <Numbers list={persons}/>
    </div>
  )
}

const Numbers = ({list}) =>{
  return(
    <div>
    {list.map((entry)=>{
      return(
      <li key={entry.name}>
        {entry.name} - Number: {entry.number}
      </li>)
    })}
    </div>
  )
}

const Found = ({results}) => {
  return(
    <>
    {results.map((entry)=>{
      return ' --- ' + entry['name'] + ': ' + entry['number']})}
    </>
  )
  }

export default App
